def iteration = 30
def count = iteration + 10

testRunner.testCase.getTestStepByName("PropertiesLoop").setPropertyValue( "success", "0" )
def successCount = 0

while(count>0) {
	testRunner.runTestStepByName("Request Test API without key")
	if ( testRunner.testCase.getTestStepByName("PropertiesLoop").getPropertyValue( "success" ).equals("1") )
	{
		successCount++
	}
	count--
}

log.info "successCount : " + successCount + " / " + iteration

if ( (successCount < iteration * 0.9) && (iteration * 1.1 < successCount) )
{
    assert false
}
else
{
    assert true
}

con:configuration.scriptText:
if ( messageExchange.getResponseStatusCode() == 200)
{
	context.testCase.getTestStepByName("PropertiesLoop").setPropertyValue( "success", "1" )
}
else
{
	if ( messageExchange.getResponseStatusCode() == 429)
	{
		context.testCase.getTestStepByName("PropertiesLoop").setPropertyValue( "success", "0" )
	}
	else
	{
		assert false
	}
}
