String remote_user = "root"
String remote_host = testRunner.testCase.testSuite.project.getPropertyValue( "E3Gateway" )
String remote_dir = "/home/e3/apache-servicemix/data/TDR/txTDR"
String tmp_tdr = "tmp_tdr"

log.info("Checking ${remote_host} for TDR files in ${remote_dir}")

def remoteExecutor = new RemoteCommandExecutor(remote_user, remote_host)
def slurper = new XmlSlurper()

File localDir = new File(tmp_tdr);
def output = remoteExecutor.listFiles("${remote_dir}")
log.info("New TDR files: [${output}]")

localDir.mkdirs();
output.eachLine {
    log.info("File: ${it}")

    String localPathname = tmp_tdr + "/" + "${it}";
    File localFile = new File(localPathname);
    localFile.delete();
    remoteExecutor.fetchFile("${remote_dir}/${it}", localPathname);

    def rootNode = slurper.parse(localFile)
    localFile.delete();
    def tdrNodes = rootNode.TDR
    log.info(tdrNodes.size())
    assert 0 < tdrNodes.size()
}
localDir.delete()


class RemoteCommandExecutor {

    private final String remoteUser;
    private final String remoteHost;

    RemoteCommandExecutor(String remoteUser, String remoteHost) {
        this.remoteUser = remoteUser;
        this.remoteHost = remoteHost;
    }

    String listFiles(String pathname) {
    	   return runCommand("ssh -o StrictHostKeyChecking=no ${remoteUser}@${remoteHost} ls ${pathname}")
    }

    String fetchFile(String remotePathname, String localPathname) {
        runCommand("scp  -o StrictHostKeyChecking=no ${remoteUser}@${remoteHost}:${remotePathname} ${localPathname}");
        return localPathname;
    }

    private String runCommand(String cmd) {
        Process p = cmd.execute();
        p.waitFor();
        if (p.exitValue()) {
            throw new RuntimeException("${cmd}: Command returned ${p.exitValue()}: {$p.err.text}");
        }
        return p.text;
    }
}
